import { AuthClient } from '@dfinity/auth-client';
import { Actor, HttpAgent } from '@dfinity/agent';
import { Principal } from '@dfinity/principal';

export interface AuthStatus {
  isAuthenticated: boolean;
  principal?: Principal;
  roles: string[];
}

class AuthService {
  private authClient: AuthClient | null = null;
  private agent: HttpAgent | null = null;
  private actor: any = null;

  async init(): Promise<void> {
    if (!this.authClient) {
      this.authClient = await AuthClient.create();
    }
  }

  async login(): Promise<void> {
    await this.init();
    
    return new Promise((resolve, reject) => {
      this.authClient!.login({
        identityProvider: process.env.REACT_APP_II_URL || 'https://identity.ic0.app',
        onSuccess: () => {
          this.setupAgent();
          resolve();
        },
        onError: (error) => {
          console.error('Login failed:', error);
          reject(error);
        },
      });
    });
  }

  async logout(): Promise<void> {
    await this.init();
    await this.authClient!.logout();
    this.agent = null;
    this.actor = null;
  }

  async checkAuth(): Promise<AuthStatus> {
    await this.init();
    
    const isAuthenticated = await this.authClient!.isAuthenticated();
    
    if (isAuthenticated) {
      const identity = this.authClient!.getIdentity();
      const principal = identity.getPrincipal();
      
      // Setup agent and actor if not already done
      if (!this.agent) {
        this.setupAgent();
      }
      
      // Get user roles from backend
      let roles: string[] = [];
      try {
        if (this.actor) {
          roles = await this.actor.get_user_roles(principal);
        }
      } catch (error) {
        console.error('Failed to fetch user roles:', error);
        roles = ['User']; // Default role
      }
      
      return {
        isAuthenticated: true,
        principal,
        roles: roles.map(role => role.toString()),
      };
    }
    
    return {
      isAuthenticated: false,
      roles: [],
    };
  }

  private setupAgent(): void {
    const identity = this.authClient!.getIdentity();
    
    this.agent = new HttpAgent({
      identity,
      host: process.env.REACT_APP_IC_HOST || 'https://ic0.app'
    });

    // Only fetch root key in development
    if (process.env.NODE_ENV === 'development') {
      this.agent.fetchRootKey().catch(err => {
        console.warn('Unable to fetch root key. Check to ensure that your local replica is running');
        console.error(err);
      });
    }

    // Create actor for backend canister
    // Note: You'll need to import the generated declarations
    // this.actor = Actor.createActor(idlFactory, {
    //   agent: this.agent,
    //   canisterId: process.env.REACT_APP_BACKEND_CANISTER_ID || 'your-canister-id',
    // });
  }

  getAgent(): HttpAgent | null {
    return this.agent;
  }

  getActor(): any {
    return this.actor;
  }

  getPrincipal(): Principal | null {
    if (this.authClient) {
      const identity = this.authClient.getIdentity();
      return identity.getPrincipal();
    }
    return null;
  }
}

export default new AuthService();
